for (let i = 0; i < radios.length; i++) {
  if (radios[i].checked) {
    console.log(radios[i].value);
  }
}

Méthodes de l'objet Math.exemple:
// Nombre aléatoire entre 1 et 10
let nombre = Math.trunc(Math.random() * 10) + 1;
console.log(nombre);

Les chaînes de caractères.exemple:
let prenom = 'Ahmed';
let nom = 'Ben Ali';
let nomComplet = prenom + ' ' + nom;
console.log(nomComplet);

let phrase = 'Bonjour le monde';
let position = phrase.indexOf('monde');
console.log(position);

let texte = 'banana';
let derniere = texte.lastIndexOf('a');
console.log(derniere);

let texte = 'Bonjour';
let sousChaine = texte.substring(0, 3);
console.log(sousChaine);

let texte = 'Bonjour le monde';
let nouveau = texte.replace('monde', 'tous');
console.log(nouveau);

let texte = 'BONJOUR';
let minuscule = texte.toLowerCase();
console.log(minuscule);

let texte = 'bonjour';
let majuscule = texte.toUpperCase();
console.log(majuscule);

let texte = '  Bonjour  ';
let nettoye = texte.trim();
console.log(nettoye);

L'objet Date.exemple:
