let jour = date.getDate();
console.log(jour);

let date = new Date();
let mois = date.getMonth() + 1;
console.log(mois);

let date = new Date();
let annee = date.getFullYear();
console.log(annee);

let date = new Date();
let texte = date.toString();
console.log(texte);

